import React, { useState } from 'react';
import Landing from './components/Landing';
import Portfolio from './components/Portfolio';
import IconsList from './components/IconsList';
import { LiaChevronDownSolid } from 'react-icons/lia';
import { LiaChevronUpSolid } from 'react-icons/lia';
import './app.css';

export default function App() {
   const [showLanding, setShowLanding] = useState(true);

   const handleChevronClick = () => {
      setShowLanding(!showLanding);
   };

   return (
      <main className='App'>
         <h2 className='logo-name text-center text-white'>Field Palmer</h2>
         <section className='container p-sm-2 p-md-5'>
            {showLanding ? <Landing /> : <Portfolio />}
            <div className='chevron-container'>
               {showLanding ? (
                  <LiaChevronUpSolid
                     className='chevron chevron-up'
                     onClick={handleChevronClick}
                  />
               ) : (
                  <LiaChevronDownSolid
                     className='chevron chevron-down'
                     onClick={handleChevronClick}
                  />
               )}
            </div>
         </section>
         <IconsList className='icon-footer' />
      </main>
   );
}
